import React from "react";
import HeroImg from "../images/hero-img.png";

// #hero-image {
//     grid-area: hero-image;
//     height: 300px;
//     width: 400px;
//     align-self: center;
//     justify-self: center;
//     padding-top: 10px;

//   }

//   .jumbotron {
//     height: 400px;
//   }

//  #hero-headline {
//   align-self: center;
//   justify-self: center;
//   font-size: 200%;
//   font-family: 'Open Sans', sans-serif;
//   color: var(--theme-color-1);
//  }

const styles = {
  heroImg: {
    height: "300px",
    width: "400px",
    alignSelf: "center",
    justifySelf: "center",
    paddingTop: "10px"

  },

  bannerText: {
    alignSelf: "center",
    justifySelf: "center",
    fontSize: "200%",
    fontFamily: "'Open Sans', sans-serif",
    color: "#7c7a7a"

  },

jumbotron: {
    height: "400px",
    backgoundColor: "#e9ecef",
}
};

function Homepage() {
  return (
    <div className="jumbotron" >
      <div className="container">
        <div className="row" >
          <div className="col-sm" style={styles.bannerText}>
            Developer and designer. Passionate about crafting sleek user
            experiences.
            <br></br>
             Welcome to my portfolio.
          </div>
          <div className="col-sm">
            <img src={HeroImg} style={styles.heroImg} />
          </div>
        </div>
      </div>

      <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <a className="navbar-brand" href="#">Marium Ladha // Web Developer </a>
      <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
        <span className="navbar-toggler-icon"></span>
      </button>
      <div className="collapse navbar-collapse" id="navbarNav">
        <ul className="navbar-nav">
          <li className="nav-item active">
            <a className="nav-link" href="#skills-section"> My skills </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#work-section"> My work </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#skills-section"> Contact me </a>
          </li>
          <li className="nav-item">
            <a className="nav-link" href="#insert-resume-link"> Resume </a>
          </li>
        </ul>
      </div>
    </nav>
    </div>

  );
}

export default Homepage;
