const styles = {
  heroImg: {
    height: "300px",
    width: "400px",
    alignSelf: "center",
    justifySelf: "center",

  },

  bannerText: {
    alignSelf: "center",
    justifySelf: "center",
    fontSize: "200%",
    fontFamily: "'Open Sans', sans-serif",

  },
};

function Homepage() {
  return (
    <div className="jumbotron">
      <div className="container">
        <div className="row">
          <div className="col-sm" src={styles.bannerText}>
            Developer and designer. Passionate about crafting sleek user
            experiences. Welcome to my portfolio.
          </div>
          <div className="col-sm">
            <img src={HeroImg} style={styles.heroImg} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Homepage;
